    const ALT_STYLES = {
      border: 'none',
      padding: '5px 15px',
      background: 'white',
    }

export default function ButtonGroup({buttons}) {
    
  const [selectedButton, setSelectedButton] = useState(0);
  
  return (
      <div className="button-group">
          <button style={selectedButton === 0 && ALT_STYLES}>$2K</button>
          <button>$5K</button>
          <button>No limit</button>
        </div>
    );
  }
