import { useState } from "react";

  /* 
    border: none;
    padding: 5px 15px;
    background: white
  */

    const ALT_STYLES = {
      border: 'none',
      padding: '5px 15px',
      background: '#745e5e',
    }

    const BUTTON_TEXT = [
      '$2k', '$5k', 'No Limit'
    ]

export default function ButtonGroup({buttons}) {
    
  const [selectedButton, setSelectedButton] = useState(0);
  
  return (
      <div className="button-group">
            {
              BUTTON_TEXT.map((btnText, i) => (
                <button 
                  onClick={() => setSelectedButton(i)}
                  style={selectedButton === 0 ? ALT_STYLES : {}}>{btnText}</button>
              ))
            }
        </div>
    );
  }
